package iitu.kz.darkness.ui.plan;

import java.util.List;
import java.util.Optional;

import com.vaadin.event.selection.SingleSelectionEvent;
import com.vaadin.navigator.View;
import com.vaadin.ui.Alignment;
import com.vaadin.ui.Button;
import com.vaadin.ui.ComboBox;
import com.vaadin.ui.FormLayout;
import com.vaadin.ui.TextField;
import com.vaadin.ui.VerticalLayout;

import iitu.kz.darkness.entities.Groups;
import iitu.kz.darkness.entities.Speciality;

@SuppressWarnings("serial")
public class PlanWindow extends VerticalLayout implements View {

    ComboBox<Groups> selectGroup = new ComboBox<>("Select Group");
    ComboBox<Speciality> selectSpec = new ComboBox<>("Select Speciality");
    TextField hourPerWeek = new TextField("Hours Per Week");

    private PlanWindowPresenter presenter;

    private long specId = -1;
    private long groupId = -1;

    public PlanWindow() {
        setupUI();
        createComboxes();
    }

    private void createComboxes() {
        List<Speciality> specialities = getSpecialities();
        selectSpec.setItems(specialities);

        // Use the name property for item captions
        selectSpec.setItemCaptionGenerator(Speciality::getName);

        selectSpec.addSelectionListener(listener -> getSelectedSpecId(listener));

        // Use the name property for item captions
        selectGroup.setItemCaptionGenerator(Groups::getName);
        
        Button btnAdd = new Button("Add");
        btnAdd.addClickListener(event -> addSpecialityPlan());
        
        hourPerWeek.setRequiredIndicatorVisible(true);
        
        FormLayout formLayout = new FormLayout();
        formLayout.setDefaultComponentAlignment(Alignment.MIDDLE_CENTER);
        formLayout.addComponents(selectSpec, selectGroup, hourPerWeek);
        
        addComponents(formLayout, btnAdd);
    }

    private void addSpecialityPlan() {
        groupId= selectGroup.getSelectedItem().get().getId();
        int hours = Integer.parseInt(hourPerWeek.getValue());
        presenter.setSpecialityPlan(specId, groupId, hours);
    }

    private void getSelectedSpecId(SingleSelectionEvent<Speciality> listener) {
//        selectGroup.setValue();
        Optional<Speciality> selectedItem = listener.getSelectedItem();
        if (selectedItem != null) {
            specId = selectedItem.get().getId();
            List<Groups> groups = getGroups(specId);
            selectGroup.setItems(groups);
        }


    }

    private List<Groups> getGroups(long specId) {
        return presenter.getSpecialityGroups(specId);
    }

    private List<Speciality> getSpecialities() {
        return presenter.getAllSpeciality();
    }

    private void setupUI() {
        this.presenter = new PlanWindowPresenter();

    }
}
